import React from 'react';

const EachItemCost = (props) => {

    const calculateEachItemCost = (expenses) => {
        for (let i = 0; i < expenses.length; i++) {
            updateItemCost(expenses[i].id, )
        }
    }

    return (
        <div>
            <p>{props.tax}</p>
            <p>{props.tip}</p>
            <button 
                onClick={() => calculateEachItemCost(props.expenses)}
            >
                Calculate Item Total
            </button>
        </div>
    )
}

export default EachItemCost;
