import React, { useState } from 'react';

import AddItem from './components/Forms/AddItem';
import MainTable from './components/MainTable'

const EXAMPLE_EXPENSES = [
  {
    id: '10000',
    name: 'sushi',
    cost: '4.23',
  }
]

const EXAMPLE_PERSONS = [
  {
    name: 'Christine',
    id: '0000010',
  }
]

const App = () => {
  const [expenses, setExpenses] = useState(EXAMPLE_EXPENSES);
  const [persons, setPersons] = useState(EXAMPLE_PERSONS)

  const addExpenseHandler = (expense) => {
    setExpenses(prevExpenses => {
      return [expense, ...prevExpenses]
    })
    console.log(expenses)
  }

  const addPersonHandler = (person) => {
    setPersons(prevPersons => {
      return [person, ...prevPersons]
    })
  }

  return (
    <div>
      <AddItem addExpense={addExpenseHandler} />
      <MainTable expenses={expenses} />
    </div>
  );
}

export default App;
