import React, { useState } from 'react';

const AddItem = (props) => {
    const [enteredName, setEnteredName] = useState('');
    const [enteredCost, setenteredCost] = useState('');

    const nameChangeHandler = (event) => {
        setEnteredName(event.target.value)
    }

    const costChangeHandler = (event) => {
        setenteredCost(event.target.value)
    }

    const createPersonMap = (persons, personMap) => {
        console.log(persons)
        for (let i = 0; i < persons.length; i++) {
            personMap.set(persons.id, false)
        }
    }

    const submitHandler = (event) => {
        event.preventDefault();
        //this is default JS behavior to prevent entire page from reloading instead of just the component

        if ( enteredName.length == 0 || enteredCost == 0 ) {
            alert("Please enter a valid name")
            setEnteredPersonName("")
            return false
        }

        const personMap = new Map()
        createPersonMap(props.persons, personMap)

        const expenseData = {
            name: enteredName,
            cost: enteredCost,
            id: Math.ceil(Math.random().toString() * 100000),
            persons: personMap
        }
        props.addExpense(expenseData)
        setEnteredName("");
        setenteredCost("");
    }

    return (
        <form onSubmit={submitHandler}>
            <div>
                <label>Item: </label>
                <input 
                    type="text"
                    name="name"
                    placeholder="Item Name"
                    value={enteredName}
                    onChange={nameChangeHandler}
                />
            </div>
            <div>
                <label>Cost: </label>
                <input 
                    type="number"
                    step="0.01"
                    min="0.01"
                    name="cost"
                    value={enteredCost}
                    placeholder="Item Cost"
                    onChange={costChangeHandler}
                />
            </div>
            <div>
                <button type='submit'>
                    Submit Item
                </button>
            </div>
        </form>
    )
}

export default AddItem;
