import React, { useState } from 'react';

import Form from './components/Form';
import MainTable from './components/MainTable';

const EXAMPLE_SUSHI_PERSIONS = new Map();
EXAMPLE_SUSHI_PERSIONS.set('00010', false);

const EXAMPLE_EXPENSES = [
  {
    id: '00001',
    name: 'sushi',
    cost: '4.23',
    persons: EXAMPLE_SUSHI_PERSIONS,
  }
]

const EXAMPLE_PERSONS = [
  {
    id: '00010',
    name: 'Christine',
  }
]

//future note: can make items and persons maps that can get updated every add person or add expense handler
//this would need to update eery time there's a new item or new person added to the list

const App = () => {
  const [expenses, setExpenses] = useState(EXAMPLE_EXPENSES);
  const [persons, setPersons] = useState(EXAMPLE_PERSONS)

  const addExpenseHandler = (expense) => {
    setExpenses(prevExpenses => {
      return [expense, ...prevExpenses]
    })
  }

  const addPersonHandler = (person) => {
    setPersons(prevPersons => {
      return [person, ...prevPersons]
    })
    setExpenses(prevExpenses => {
      for (let i = 0; i < prevExpenses.length; i++) {
        console.log(person)
        prevExpenses[i].persons.set(person.id, false)
      }
      return prevExpenses
    })
  }

  const updateItemEaten = (item, person) => {
    setExpenses(prevExpenses => {
      for (let i  = 0; i < prevExpenses.length; i++) {
        let currentItem = prevExpenses[i]
        console.log(`before: ${currentItem}`)
        if (currentItem.id === item) {
          currentItem.persons.get(person) ? 
            currentItem.persons.set( person, false ) : 
            currentItem.persons.set( person , true )
        }
      }
      console.log(`after: ${currentItem}`)
      return prevExpenses
    })
  }

  return (
    <div>
      <Form 
        addExpense={addExpenseHandler}
        addPerson={addPersonHandler}
        persons={persons}
      />
      <MainTable 
        expenses={expenses} 
        persons={persons}
        updateItemEaten={updateItemEaten}
      />
    </div>
  );
}

export default App;
